// Parse AI recommendations and apply business rules
const aiRecommendations = JSON.parse($input.first().json.choices[0].message.content);
const businessRules = {
  minMargin: 0.15, // 15% minimum margin
  maxPriceChange: 0.10, // Max 10% price change at once
  buyBoxThreshold: 0.01 // Stay within $0.01 of Buy Box
};

const finalPrices = [];

for (const rec of aiRecommendations) {
  let finalPrice = rec.recommended_price;

  // Apply business rule constraints
  const currentPrice = rec.current_price;
  const maxChange = currentPrice * businessRules.maxPriceChange;

  if (Math.abs(finalPrice - currentPrice) > maxChange) {
    finalPrice = currentPrice + (finalPrice > currentPrice ? maxChange : -maxChange);
  }

  // Ensure minimum margin
  const cost = currentPrice * 0.7; // Assume 70% cost basis
  const minPrice = cost / (1 - businessRules.minMargin);
  finalPrice = Math.max(finalPrice, minPrice);

  finalPrices.push({
    asin: rec.asin,
    current_price: currentPrice,
    recommended_price: finalPrice,
    price_change: ((finalPrice - currentPrice) / currentPrice * 100).toFixed(2) + '%',
    reasoning: rec.reasoning,
    confidence: rec.confidence_score,
    action: Math.abs(finalPrice - currentPrice) > 0.01 ? 'UPDATE' : 'HOLD'
  });
}

return finalPrices.map(item => ({json: item}));
