// Parse AI decision and validate inventory availability
const orderData = $input.first().json;
const aiDecision = JSON.parse($input.last().json.choices[0].message.content);

// Mock inventory check (replace with real inventory API)
const inventoryCheck = {
  'West Coast DC': { available: true, quantity: 50, cost: 4.50 },
  'East Coast DC': { available: true, quantity: 23, cost: 4.25 },
  'Central DC': { available: false, quantity: 0, cost: 4.75 },
  '3PL Partner': { available: true, quantity: 100, cost: 6.50 },
  'Amazon FBA': { available: true, quantity: 75, cost: 3.25 }
};

// Validate AI recommendation against actual inventory
const recommendedFacility = aiDecision.recommended_facility;
const inventory = inventoryCheck[recommendedFacility];

let finalDecision = aiDecision;

// If recommended facility is out of stock, use backup
if (!inventory || !inventory.available) {
  const backupOptions = aiDecision.backup_options || [];
  for (const backup of backupOptions) {
    if (inventoryCheck[backup] && inventoryCheck[backup].available) {
      finalDecision.recommended_facility = backup;
      finalDecision.reasoning += ` (Switched to ${backup} due to inventory))`;
      break;
    }
  }
}

// Enrich with actual inventory data
const finalInventory = inventoryCheck[finalDecision.recommended_facility];
finalDecision.actual_cost = finalInventory.cost;
finalDecision.available_quantity = finalInventory.quantity;

// Add order processing metadata
finalDecision.order_id = orderData.id || orderData.order_number;
finalDecision.customer_email = orderData.email || orderData.customer?.email;
finalDecision.shipping_address = orderData.shipping_address;
finalDecision.items = orderData.line_items || orderData.items;
finalDecision.priority = orderData.tags?.includes('VIP') ? 'HIGH' : 'NORMAL';
finalDecision.processing_timestamp = new Date().toISOString();

return [{json: finalDecision}];

