// Enhanced CLV calculation with ML features
const analysis = JSON.parse($input.first().json.choices[0].message.content);
const customerData = $node["customer-data"].json.customer;
const orders = $node["order-history"].json.orders;

// Calculate additional ML features
function calculateMLFeatures(orders) {
  const now = new Date();
  const orderDates = orders.map(o => new Date(o.created_at)).sort((a, b) => b - a);

  // Recency (days since last order)
  const recency = orderDates.length > 0 ? Math.floor((now - orderDates[0]) / (1000 * 60 * 60 * 24)) : 9999;

  // Frequency (orders per month)
  const frequency = orders.length / Math.max(1, (now - new Date(customerData.created_at)) / (1000 * 60 * 60 * 24 * 30));

  // AOV progression
  const aovTrend = orders.length > 1 ? (orders.slice(0, 3).reduce((sum, o) => sum + parseFloat(o.total_price), 0) / 3) - 
                   (orders.slice(-3).reduce((sum, o) => sum + parseFloat(o.total_price), 0) / 3) : 0;

  // Category diversity
  const categorySet = new Set();
  orders.forEach(order => {
    order.line_items?.forEach(item => {
      if (item.product_type) categorySet.add(item.product_type);
    });
  });

  return {
    recency,
    frequency: frequency.toFixed(2),
    monetary: parseFloat(customerData.total_spent || 0),
    aov_trend: aovTrend.toFixed(2),
    category_diversity: categorySet.size,
    order_consistency: calculateConsistency(orderDates)
  };
}

function calculateConsistency(dates) {
  if (dates.length < 2) return 0;
  const intervals = [];
  for (let i = 1; i < dates.length; i++) {
    intervals.push((dates[i-1] - dates[i]) / (1000 * 60 * 60 * 24));
  }
  const avgInterval = intervals.reduce((sum, i) => sum + i, 0) / intervals.length;
  const variance = intervals.reduce((sum, i) => sum + Math.pow(i - avgInterval, 2), 0) / intervals.length;
  return Math.max(0, 100 - Math.sqrt(variance) / avgInterval * 100);
}

const mlFeatures = calculateMLFeatures(orders);

// Enhanced segmentation logic
let segment = analysis.segment;
let priority = 'medium';

if (mlFeatures.monetary > 1000 && mlFeatures.frequency > 2 && mlFeatures.recency < 60) {
  segment = 'VIP';
  priority = 'high';
} else if (mlFeatures.recency > 180 && mlFeatures.monetary > 500) {
  segment = 'At-Risk-VIP';
  priority = 'urgent';
} else if (mlFeatures.recency < 30 && orders.length === 1) {
  segment = 'New-High-Intent';
  priority = 'high';
}

const result = {
  customer_id: customerData.id,
  email: customerData.email,
  clv_current: mlFeatures.monetary,
  clv_predicted: analysis.clv_predicted || mlFeatures.monetary * (mlFeatures.frequency * 12),
  segment: segment,
  priority: priority,
  engagement_score: analysis.engagement_score || Math.min(100, mlFeatures.frequency * 20 + (100 - mlFeatures.recency/3)),
  ml_features: mlFeatures,
  rfm_scores: analysis.rfm_scores,
  recommendations: analysis.recommendations,
  next_actions: analysis.next_best_actions,
  updated_at: new Date().toISOString()
};

return [{json: result}];

