// Advanced time series forecasting with ARIMA-like calculations
const analysis = JSON.parse($input.first().json.choices[0].message.content);
const salesHistory = $node["sales-history"].json;
const trendsData = $node["google-trends"].json;

// Time series analysis functions
function calculateMovingAverage(data, window) {
  const result = [];
  for (let i = window - 1; i < data.length; i++) {
    const sum = data.slice(i - window + 1, i + 1).reduce((a, b) => a + b, 0);
    result.push(sum / window);
  }
  return result;
}

function calculateSeasonality(data) {
  const dayOfWeek = {};
  const monthOfYear = {};

  data.forEach(item => {
    const date = new Date(item.date);
    const dow = date.getDay();
    const month = date.getMonth();

    if (!dayOfWeek[dow]) dayOfWeek[dow] = [];
    if (!monthOfYear[month]) monthOfYear[month] = [];

    dayOfWeek[dow].push(item.units_sold);
    monthOfYear[month].push(item.units_sold);
  });

  // Calculate seasonal multipliers
  const weeklyPattern = Object.keys(dayOfWeek).reduce((acc, day) => {
    acc[day] = dayOfWeek[day].reduce((sum, val) => sum + val, 0) / dayOfWeek[day].length;
    return acc;
  }, {});

  return { weeklyPattern, monthOfYear };
}

function forecastARIMA(data, periods) {
  // Simplified ARIMA-like forecast
  const recent = data.slice(-14); // Last 14 days
  const trend = (recent[recent.length - 1] - recent[0]) / recent.length;
  const baseline = recent.reduce((sum, val) => sum + val, 0) / recent.length;

  const forecast = [];
  for (let i = 1; i <= periods; i++) {
    const trendComponent = baseline + (trend * i);
    const seasonalMultiplier = 1 + (Math.sin(i * Math.PI / 7) * 0.1); // Weekly seasonality
    const randomWalk = Math.random() * 0.1 - 0.05; // Small random component

    forecast.push(Math.max(0, trendComponent * seasonalMultiplier * (1 + randomWalk)));
  }

  return forecast;
}

// Process each product category
const productCategories = {};
salesHistory.forEach(item => {
  if (!productCategories[item.category]) {
    productCategories[item.category] = [];
  }
  productCategories[item.category].push({
    date: item.date,
    units_sold: item.units_sold,
    revenue: item.revenue
  });
});

const enhancedForecasts = [];

Object.keys(productCategories).forEach(category => {
  const categoryData = productCategories[category].sort((a, b) => new Date(a.date) - new Date(b.date));
  const unitsSold = categoryData.map(d => d.units_sold);

  // Calculate technical indicators
  const ma7 = calculateMovingAverage(unitsSold, 7);
  const ma30 = calculateMovingAverage(unitsSold, 30);
  const seasonality = calculateSeasonality(categoryData);

  // Generate forecasts
  const forecast30 = forecastARIMA(unitsSold, 30);
  const forecast90 = forecastARIMA(unitsSold, 90);

  // Calculate confidence intervals
  const recentVolatility = ma7.slice(-7).reduce((sum, val, i) => {
    return sum + Math.abs(val - unitsSold[unitsSold.length - 7 + i]);
  }, 0) / 7;

  const confidence = recentVolatility < 10 ? 'high' : recentVolatility < 25 ? 'medium' : 'low';

  // Incorporate external signals
  const trendMultiplier = trendsData?.default?.trendingSearchesDays?.[0]?.trendingSearches?.some(t => 
    t.title.query.toLowerCase().includes(category.toLowerCase())) ? 1.2 : 1.0;

  enhancedForecasts.push({
    category: category,
    forecast_30_days: {
      units: forecast30.reduce((sum, val) => sum + val * trendMultiplier, 0).toFixed(0),
      revenue: (forecast30.reduce((sum, val) => sum + val * trendMultiplier, 0) * 
               (categoryData.reduce((sum, d) => sum + d.revenue, 0) / categoryData.reduce((sum, d) => sum + d.units_sold, 0))).toFixed(2)
    },
    forecast_90_days: {
      units: forecast90.reduce((sum, val) => sum + val * trendMultiplier, 0).toFixed(0),
      revenue: (forecast90.reduce((sum, val) => sum + val * trendMultiplier, 0) * 
               (categoryData.reduce((sum, d) => sum + d.revenue, 0) / categoryData.reduce((sum, d) => sum + d.units_sold, 0))).toFixed(2)
    },
    confidence: confidence,
    trend_direction: ma7[ma7.length - 1] > ma30[ma30.length - 1] ? 'up' : 'down',
    seasonal_multiplier: trendMultiplier,
    volatility: recentVolatility.toFixed(2),
    recommendations: analysis.forecasts?.find(f => f.category === category)?.recommendations || [],
    risk_factors: analysis.forecasts?.find(f => f.category === category)?.risks || [],
    updated_at: new Date().toISOString()
  });
});

return enhancedForecasts.map(forecast => ({json: forecast}));

