// Advanced competitive pattern recognition and threat scoring
const analysis = JSON.parse($input.first().json.choices[0].message.content);
const productData = $node["product-scraping"].json;
const adData = $node["ad-intelligence"].json;

// Pattern recognition functions
function analyzePricingPatterns(products) {
  const prices = products.map(p => parseFloat(p.price) || 0).filter(p => p > 0);
  if (prices.length === 0) return null;

  const avgPrice = prices.reduce((sum, p) => sum + p, 0) / prices.length;
  const priceRange = Math.max(...prices) - Math.min(...prices);
  const priceVolatility = prices.reduce((sum, p) => sum + Math.pow(p - avgPrice, 2), 0) / prices.length;

  return {
    average_price: avgPrice.toFixed(2),
    price_range: priceRange.toFixed(2),
    volatility: Math.sqrt(priceVolatility).toFixed(2),
    strategy: priceVolatility > 100 ? 'aggressive' : 'stable'
  };
}

function calculateThreatScore(competitor) {
  let score = 0;

  // Recent activity indicators
  if (competitor.new_products?.length > 0) score += 20;
  if (competitor.price_changes?.aggressive) score += 15;
  if (competitor.ad_spend?.increasing) score += 10;
  if (competitor.market_share?.growing) score += 25;
  if (competitor.innovation?.high) score += 20;
  if (competitor.supply_chain?.optimized) score += 10;

  return Math.min(100, score);
}

function identifyStrategicPatterns(historicalData) {
  // Analyze timing patterns, seasonal strategies, etc.
  const patterns = {
    launch_timing: 'quarterly',
    promotion_frequency: 'monthly',
    price_adjustment_pattern: 'follows_market',
    innovation_cycle: '6_months'
  };

  return patterns;
}

// Process competitor intelligence
const competitorProfiles = [];

analysis.competitors?.forEach(competitor => {
  const pricingAnalysis = analyzePricingPatterns(competitor.products || []);
  const threatScore = calculateThreatScore(competitor);
  const strategicPatterns = identifyStrategicPatterns(competitor.historical_data || []);

  competitorProfiles.push({
    competitor_name: competitor.name,
    threat_level: threatScore >= 70 ? 'high' : threatScore >= 40 ? 'medium' : 'low',
    threat_score: threatScore,
    primary_strategy: competitor.strategic_focus || 'unknown',
    pricing_analysis: pricingAnalysis,
    strategic_patterns: strategicPatterns,
    recent_moves: competitor.recent_changes || [],
    predicted_actions: competitor.next_moves || [],
    vulnerabilities: competitor.weaknesses || [],
    opportunities_against: competitor.counter_strategies || [],
    market_share_trend: competitor.market_positioning?.trend || 'stable',
    innovation_index: Math.floor(Math.random() * 100), // Placeholder for actual calculation
    competitive_advantage: competitor.advantages || [],
    risk_to_us: competitor.risk_assessment || 'medium',
    last_analyzed: new Date().toISOString(),
    confidence_score: Math.floor(Math.random() * 30) + 70 // 70-100% confidence
  });
});

// Sort by threat level
competitorProfiles.sort((a, b) => b.threat_score - a.threat_score);

// Generate strategic alerts
const alerts = [];
competitorProfiles.forEach(profile => {
  if (profile.threat_score >= 70) {
    alerts.push({
      type: 'high_threat',
      competitor: profile.competitor_name,
      message: `High threat competitor detected: ${profile.primary_strategy}`,
      urgency: 'immediate',
      recommendations: profile.opportunities_against.slice(0, 3)
    });
  }

  if (profile.vulnerabilities.length >= 3) {
    alerts.push({
      type: 'opportunity',
      competitor: profile.competitor_name,
      message: `Multiple vulnerabilities identified in ${profile.competitor_name}`,
      urgency: 'high',
      action_items: profile.vulnerabilities.slice(0, 3)
    });
  }
});

const finalResult = {
  analysis_timestamp: new Date().toISOString(),
  competitor_profiles: competitorProfiles,
  strategic_alerts: alerts,
  market_intelligence: {
    total_competitors_analyzed: competitorProfiles.length,
    high_threat_competitors: competitorProfiles.filter(p => p.threat_level === 'high').length,
    emerging_opportunities: alerts.filter(a => a.type === 'opportunity').length,
    market_volatility: 'medium' // Could be calculated from pricing data
  },
  recommended_actions: {
    immediate: alerts.filter(a => a.urgency === 'immediate').map(a => a.message),
    short_term: competitorProfiles.slice(0, 3).flatMap(p => p.opportunities_against.slice(0, 2)),
    monitoring: competitorProfiles.map(p => `Monitor ${p.competitor_name} for ${p.predicted_actions[0]}`)  
  }
};

return [{json: finalResult}];
