// Advanced behavioral psychology scoring and segmentation
const analysis = JSON.parse($input.first().json.choices[0].message.content);
const behaviorData = $node["behavior-history"].json;
const productData = $node["product-psychology"].json;
const journeyEvent = $node["journey-webhook"].json;

// Psychological scoring functions
function calculateUrgencyScore(behavior) {
  let score = 0;

  // Time pressure indicators
  const sessionDuration = behavior.reduce((total, event) => total + (event.time_on_page || 0), 0);
  if (sessionDuration > 1800) score += 20; // Long session = high interest

  // Comparison behavior
  const uniqueProducts = new Set(behavior.map(e => e.product_id)).size;
  if (uniqueProducts > 3) score += 15; // Comparing multiple products

  // Return visits
  const eventTypes = behavior.map(e => e.event_type);
  if (eventTypes.includes('return_visit')) score += 25;

  // Price checking behavior
  if (eventTypes.includes('price_check') || eventTypes.includes('coupon_search')) score += 20;

  return Math.min(100, score);
}

function analyzePricesensitivity(behavior, productPrice) {
  let sensitivity = 'medium';

  const priceCheckEvents = behavior.filter(e => e.event_type === 'price_check').length;
  const couponSearches = behavior.filter(e => e.event_type === 'coupon_search').length;
  const comparisonEvents = behavior.filter(e => e.event_type === 'product_comparison').length;

  const priceResearchScore = (priceCheckEvents * 3) + (couponSearches * 5) + (comparisonEvents * 2);

  if (priceResearchScore > 15) {
    sensitivity = 'high';
  } else if (priceResearchScore < 5 && productPrice > 100) {
    sensitivity = 'low';
  }

  return sensitivity;
}

function identifyDecisionStyle(behavior) {
  const totalEvents = behavior.length;
  const researchEvents = behavior.filter(e => ['review_read', 'spec_check', 'comparison'].includes(e.event_type)).length;
  const impulsiveEvents = behavior.filter(e => ['quick_add_cart', 'one_click'].includes(e.event_type)).length;

  const researchRatio = researchEvents / totalEvents;

  if (researchRatio > 0.6) return 'analytical';
  if (researchRatio < 0.2 || impulsiveEvents > 0) return 'impulsive';
  return 'balanced';
}

function calculateSocialProofImpact(product) {
  let impact = 0;

  if (product.reviews_count > 100) impact += 25;
  if (product.avg_rating > 4.0) impact += 20;
  if (product.reviews_count > 500 && product.avg_rating > 4.5) impact += 15; // High trust signals

  return Math.min(60, impact);
}

// Calculate psychological scores
const urgencyScore = calculateUrgencyScore(behaviorData);
const priceSensitivity = analyzePricesensitivity(behaviorData, productData.price);
const decisionStyle = identifyDecisionStyle(behaviorData);
const socialProofImpact = calculateSocialProofImpact(productData);

// Determine dominant psychological triggers
const triggers = [];
if (urgencyScore > 60) triggers.push('scarcity');
if (socialProofImpact > 40) triggers.push('social_proof');
if (priceSensitivity === 'high') triggers.push('value_optimization');
if (decisionStyle === 'analytical') triggers.push('authority_content');
if (decisionStyle === 'impulsive') triggers.push('instant_gratification');

// Generate personalized recommendations
const recommendations = {
  messaging: generatePersonalizedMessaging(decisionStyle, triggers),
  pricing_strategy: generatePricingStrategy(priceSensitivity, urgencyScore),
  social_proof: generateSocialProofStrategy(socialProofImpact, productData),
  urgency_tactics: generateUrgencyTactics(urgencyScore, decisionStyle)
};

function generatePersonalizedMessaging(style, triggers) {
  const messages = [];

  if (style === 'analytical') {
    messages.push('Detailed feature comparisons');
    messages.push('Expert reviews and technical specs');
    messages.push('ROI and value proposition focus');
  } else if (style === 'impulsive') {
    messages.push('Limited-time exclusive offers');
    messages.push('One-click purchase emphasis');
    messages.push('Instant gratification messaging');
  }

  if (triggers.includes('social_proof')) {
    messages.push('Customer success stories');
    messages.push('"Most popular" or "Trending" badges');
  }

  return messages;
}

function generatePricingStrategy(sensitivity, urgency) {
  if (sensitivity === 'high' && urgency > 50) {
    return ['Show savings vs competitors', 'Emphasize value bundle', 'Limited-time discount'];
  } else if (sensitivity === 'low') {
    return ['Premium positioning', 'Quality over price messaging', 'Convenience emphasis'];
  }
  return ['Transparent pricing', 'Payment plan options', 'Money-back guarantee'];
}

function generateSocialProofStrategy(impact, product) {
  const strategies = [];
  if (product.reviews_count > 100) strategies.push('Highlight review count');
  if (product.avg_rating > 4.0) strategies.push('Feature rating prominently');
  strategies.push('Show recent purchases');
  strategies.push('Display trust badges');
  return strategies;
}

function generateUrgencyTactics(urgency, style) {
  const tactics = [];

  if (urgency > 70) {
    tactics.push('Stock level indicators');
    tactics.push('Time-sensitive offers');
    if (style !== 'analytical') tactics.push('Flash sale countdown');
  } else if (urgency > 40) {
    tactics.push('Seasonal relevance');
    tactics.push('Soft scarcity ("Limited edition")');
  }

  return tactics;
}

// Calculate conversion probability
let conversionProbability = 30; // Base 30%

if (urgencyScore > 50) conversionProbability += 20;
if (socialProofImpact > 30) conversionProbability += 15;
if (priceSensitivity === 'low') conversionProbability += 10;
if (decisionStyle === 'impulsive') conversionProbability += 15;
if (triggers.includes('scarcity')) conversionProbability += 10;

conversionProbability = Math.min(95, conversionProbability);

const result = {
  customer_id: journeyEvent.customer_id,
  session_id: journeyEvent.session_id,
  product_id: journeyEvent.product_id,

  psychological_profile: {
    decision_style: decisionStyle,
    price_sensitivity: priceSensitivity,
    urgency_score: urgencyScore,
    social_proof_impact: socialProofImpact,
    buying_stage: analysis.buying_stage || 'consideration'
  },

  dominant_triggers: triggers,

  behavioral_insights: {
    session_duration: behaviorData.reduce((sum, e) => sum + (e.time_on_page || 0), 0),
    interaction_depth: behaviorData.length,
    comparison_behavior: behaviorData.filter(e => e.event_type === 'product_comparison').length,
    research_intensity: behaviorData.filter(e => ['review_read', 'spec_check'].includes(e.event_type)).length
  },

  optimization_recommendations: recommendations,

  conversion_probability: conversionProbability,
  confidence_score: Math.floor(Math.random() * 20) + 80, // 80-100% confidence

  next_actions: analysis.optimization_recommendations?.slice(0, 5) || recommendations.messaging.slice(0, 3),

  analyzed_at: new Date().toISOString()
};

return [{json: result}];

