// Advanced churn prediction with ML-like scoring
const analysis = JSON.parse($input.first().json.choices[0].message.content);
const customerData = $node["customer-behavior"].json;
const supportData = $node["support-data"].json;
const engagementData = $node["engagement-data"].json;

// Create lookup maps for faster data access
const supportMap = new Map(supportData.map(s => [s.customer_id, s]));
const engagementMap = new Map(engagementData.map(e => [e.customer_id, e]));

// Advanced churn scoring algorithms
function calculateRFMScore(customer) {
  // Recency Score (0-100)
  const daysSinceLastOrder = customer.days_since_last_order;
  let recencyScore = 100;
  if (daysSinceLastOrder > 180) recencyScore = 20;
  else if (daysSinceLastOrder > 120) recencyScore = 40;
  else if (daysSinceLastOrder > 90) recencyScore = 60;
  else if (daysSinceLastOrder > 60) recencyScore = 80;

  // Frequency Score (0-100)
  const avgOrdersPerMonth = customer.orders_count / customer.active_months;
  let frequencyScore = Math.min(100, avgOrdersPerMonth * 25);

  // Monetary Score (0-100)
  const avgOrderValue = parseFloat(customer.avg_order_value || 0);
  let monetaryScore = Math.min(100, avgOrderValue / 5); // Normalize to $500 = 100pts

  return {
    recency: recencyScore,
    frequency: frequencyScore,
    monetary: monetaryScore,
    composite: (recencyScore + frequencyScore + monetaryScore) / 3
  };
}

function calculateEngagementScore(customerId) {
  const engagement = engagementMap.get(customerId);
  if (!engagement) return 0;

  const openRate = engagement.open_rate || 0;
  const clickRate = engagement.email_clicks / Math.max(1, engagement.email_opens) || 0;
  const recentEngagement = engagement.last_email_engagement ? 
    Math.max(0, 100 - (new Date() - new Date(engagement.last_email_engagement)) / (1000 * 60 * 60 * 24)) : 0;

  return (openRate * 40) + (clickRate * 100 * 30) + (recentEngagement * 0.3);
}

function calculateSatisfactionScore(customerId) {
  const support = supportMap.get(customerId);
  if (!support) return 75; // Neutral if no support history

  let score = 50;

  // High satisfaction from previous interactions
  if (support.avg_satisfaction >= 4.0) score += 30;
  else if (support.avg_satisfaction >= 3.0) score += 10;
  else if (support.avg_satisfaction < 2.0) score -= 30;

  // Resolution rate impact
  if (support.resolution_rate >= 0.8) score += 15;
  else if (support.resolution_rate < 0.5) score -= 15;

  // High priority tickets indicate problems
  if (support.high_priority_tickets > 2) score -= 20;

  return Math.max(0, Math.min(100, score));
}

function predictChurnProbability(rfm, engagement, satisfaction, customer) {
  let churnScore = 0;

  // RFM contribution (40% weight)
  churnScore += (100 - rfm.composite) * 0.4;

  // Engagement contribution (30% weight)
  churnScore += (100 - engagement) * 0.3;

  // Satisfaction contribution (20% weight)
  churnScore += (100 - satisfaction) * 0.2;

  // Additional risk factors (10% weight)
  if (customer.days_since_last_order > 120) churnScore += 10;
  if (customer.total_spent > 500 && customer.days_since_last_order > 90) churnScore += 5; // VIP at risk
  if (customer.orders_count === 2 && customer.days_since_last_order > 60) churnScore += 15; // Early customer drop-off

  return Math.min(100, churnScore);
}

function determineRiskLevel(churnProbability, customerValue) {
  if (churnProbability >= 80) return 'critical';
  if (churnProbability >= 65) return 'high';
  if (churnProbability >= 40) return 'medium';
  return 'low';
}

function generateRetentionStrategy(customer, churnProbability, rfm, engagement, satisfaction) {
  const strategies = [];

  // High churn probability strategies
  if (churnProbability >= 70) {
    strategies.push('Immediate personal outreach call');
    strategies.push('Exclusive discount offer (15-20%)');
    if (customer.total_spent > 300) strategies.push('VIP customer service upgrade');
  }

  // Low engagement strategies
  if (engagement < 30) {
    strategies.push('Re-engagement email campaign');
    strategies.push('Personalized product recommendations');
    strategies.push('Preference center update');
  }

  // Low satisfaction strategies
  if (satisfaction < 50) {
    strategies.push('Customer success manager assignment');
    strategies.push('Satisfaction survey and feedback loop');
    strategies.push('Proactive issue resolution');
  }

  // RFM-based strategies
  if (rfm.frequency < 30) {
    strategies.push('Purchase frequency incentives');
    strategies.push('Subscription or membership offer');
  }

  if (rfm.monetary < 40) {
    strategies.push('Bundle offers and upselling');
    strategies.push('Value-focused messaging');
  }

  return strategies.slice(0, 5); // Top 5 strategies
}

// Process each customer
const churnPredictions = customerData.map(customer => {
  const rfmScores = calculateRFMScore(customer);
  const engagementScore = calculateEngagementScore(customer.customer_id);
  const satisfactionScore = calculateSatisfactionScore(customer.customer_id);
  const churnProbability = predictChurnProbability(rfmScores, engagementScore, satisfactionScore, customer);
  const riskLevel = determineRiskLevel(churnProbability, customer.total_spent);
  const retentionStrategy = generateRetentionStrategy(customer, churnProbability, rfmScores, engagementScore, satisfactionScore);

  // Calculate timeline to churn
  let daysToChurn = 90;
  if (churnProbability >= 80) daysToChurn = 30;
  else if (churnProbability >= 65) daysToChurn = 60;

  return {
    customer_id: customer.customer_id,
    email: customer.email,
    first_name: customer.first_name,
    last_name: customer.last_name,

    churn_probability: Math.round(churnProbability),
    risk_level: riskLevel,
    days_to_predicted_churn: daysToChurn,

    scores: {
      rfm_composite: Math.round(rfmScores.composite),
      engagement: Math.round(engagementScore),
      satisfaction: Math.round(satisfactionScore)
    },

    customer_metrics: {
      total_spent: parseFloat(customer.total_spent),
      orders_count: customer.orders_count,
      days_since_last_order: customer.days_since_last_order,
      avg_order_value: parseFloat(customer.avg_order_value || 0),
      clv_at_risk: parseFloat(customer.total_spent) * (customer.orders_count >= 5 ? 1.5 : 1.2)
    },

    retention_strategies: retentionStrategy,

    next_actions: [
      `Contact within ${daysToChurn <= 30 ? '24 hours' : '7 days'}`,
      retentionStrategy[0] || 'Send re-engagement email',
      'Monitor response and adjust strategy'
    ],

    priority_score: Math.round((churnProbability * 0.7) + (parseFloat(customer.total_spent) / 100 * 0.3)),

    analyzed_at: new Date().toISOString()
  };
});

// Sort by priority score (highest risk + highest value first)
churnPredictions.sort((a, b) => b.priority_score - a.priority_score);

return churnPredictions.map(prediction => ({json: prediction}));
