// Parse AI response and enrich with customer data
const actionData = $input.first().json;
const loyaltyStrategy = JSON.parse($input.last().json.choices[0].message.content);

// Enhanced customer profile with loyalty data
const customerProfile = {
  customer_id: actionData.customer_id,
  email: actionData.customer_email,
  first_name: actionData.customer_name || 'Valued Customer',
  current_tier: loyaltyStrategy.customer_tier,
  lifetime_value: actionData.lifetime_spent || 0,
  total_orders: actionData.order_count || 0,
  current_points_balance: actionData.current_points || 0,
  referrals_made: actionData.referral_count || 0,
  reviews_written: actionData.review_count || 0,
  last_purchase_date: actionData.last_order_date || null,
  preferred_categories: actionData.favorite_categories || ['General'],
  communication_preferences: actionData.preferences || {
    email: true,
    sms: false,
    push: true
  }
};

// Calculate new points balance
const newPointsBalance = customerProfile.current_points_balance + loyaltyStrategy.total_points_awarded;

// Determine tier progression
const tierRequirements = {
  'Bronze': { orders: 3, spending: 150 },
  'Silver': { orders: 10, spending: 500 },
  'Gold': { orders: 25, spending: 1500 },
  'Platinum': { orders: 50, spending: 3000 }
};

const checkTierUpgrade = (profile) => {
  const currentOrders = profile.total_orders;
  const currentSpending = profile.lifetime_value;

  for (const [tier, requirements] of Object.entries(tierRequirements)) {
    if (currentOrders >= requirements.orders && currentSpending >= requirements.spending) {
      if (tier !== profile.current_tier) {
        return {
          upgraded: true,
          new_tier: tier,
          upgrade_bonus: {
            'Bronze': 100,
            'Silver': 250,
            'Gold': 500,
            'Platinum': 1000
          }[tier]
        };
      }
    }
  }
  return { upgraded: false };
};

const tierUpgrade = checkTierUpgrade(customerProfile);

// Generate milestone achievements
const milestones = {
  'first_review': customerProfile.reviews_written === 1 ? { achieved: true, bonus: 100, message: 'First Review Milestone!' } : null,
  'fifth_order': customerProfile.total_orders === 5 ? { achieved: true, bonus: 200, message: 'Frequent Shopper Milestone!' } : null,
  'first_referral': customerProfile.referrals_made === 1 ? { achieved: true, bonus: 150, message: 'Brand Ambassador Milestone!' } : null,
  'spending_500': customerProfile.lifetime_value >= 500 && customerProfile.lifetime_value < 600 ? { achieved: true, bonus: 300, message: 'Spending Champion!' } : null
};

const achievedMilestones = Object.entries(milestones)
  .filter(([key, milestone]) => milestone && milestone.achieved)
  .map(([key, milestone]) => milestone);

// Calculate final points including milestones and tier upgrades
let finalPointsAwarded = loyaltyStrategy.total_points_awarded;
let bonusMessages = [];

if (tierUpgrade.upgraded) {
  finalPointsAwarded += tierUpgrade.upgrade_bonus;
  bonusMessages.push(`ğŸ‰ Congratulations! You've been upgraded to ${tierUpgrade.new_tier} tier!`);
}

achievedMilestones.forEach(milestone => {
  finalPointsAwarded += milestone.bonus;
  bonusMessages.push(`ğŸ† ${milestone.message} +${milestone.bonus} bonus points!`);
});

// Create reward notification content
const rewardNotification = {
  customer_profile: {
    ...customerProfile,
    new_points_balance: newPointsBalance + (tierUpgrade.upgraded ? tierUpgrade.upgrade_bonus : 0) + achievedMilestones.reduce((sum, m) => sum + m.bonus, 0),
    new_tier: tierUpgrade.upgraded ? tierUpgrade.new_tier : customerProfile.current_tier
  },
  action_details: {
    action_type: loyaltyStrategy.action_category,
    base_points: loyaltyStrategy.base_points_earned,
    multiplier: loyaltyStrategy.bonus_multiplier,
    total_points: finalPointsAwarded,
    action_description: actionData.action_description || `Earned points for ${loyaltyStrategy.action_category}`
  },
  tier_upgrade: tierUpgrade,
  milestones_achieved: achievedMilestones,
  bonus_messages: bonusMessages,
  next_milestone: loyaltyStrategy.next_milestone_info,
  personalized_message: loyaltyStrategy.personalized_message,
  suggested_actions: loyaltyStrategy.suggested_actions,
  special_rewards: loyaltyStrategy.special_rewards || [],
  created_at: new Date().toISOString()
};

return [{json: rewardNotification}];
