parameters.jsCode:
// Aggregate and deduplicate findings from AI analysis
const aiResponse = JSON.parse($input.first().json.choices[0].message.content);

// Remove duplicates and filter low-impact events
const significantEvents = aiResponse.filter(event => 
  event.market_impact !== 'LOW' && 
  event.urgency_level !== 'LOW'
);

// Add metadata and timestamps
significantEvents.forEach(event => {
  event.detected_at = new Date().toISOString();
  event.source = 'AI_Analysis';
  event.notification_priority = 
    event.urgency_level === 'CRITICAL' ? 'IMMEDIATE' :
    event.market_impact === 'HIGH' ? 'HIGH' : 'MEDIUM';
});

// Group by event type for different handling
const grouped = {
  launches: significantEvents.filter(e => e.event_type === 'LAUNCH'),
  drops: significantEvents.filter(e => e.event_type === 'DROP'),
  stockouts: significantEvents.filter(e => e.event_type === 'STOCKOUT')
};

return Object.entries(grouped).map(([type, events]) => ({
  json: { 
    event_category: type, 
    events: events, 
    count: events.length,
    timestamp: new Date().toISOString()
  }
})).filter(item => item.json.count > 0);

