// Process AI insights and create actionable funnel metrics
const aiResponse = JSON.parse($input.first().json.choices[0].message.content);

const funnelMetrics = {
  analysis_timestamp: new Date().toISOString(),
  summary: aiResponse.funnel_performance_summary,
  segments: aiResponse.customer_segmentation,
  recommendations: aiResponse.optimization_recommendations,
  predictions: aiResponse.predictive_analytics,
  key_metrics: {
    total_sessions: aiResponse.total_sessions || 0,
    conversion_rate: aiResponse.overall_conversion_rate || 0,
    average_order_value: aiResponse.average_order_value || 0,
    customer_lifetime_value: aiResponse.customer_lifetime_value || 0
  },
  alerts: aiResponse.critical_alerts || [],
  priority_actions: aiResponse.immediate_actions || []
};

// Calculate funnel efficiency
const funnelEfficiency = {
  awareness_to_consideration: (funnelMetrics.key_metrics.conversion_rate * 100) / 2,
  consideration_to_purchase: funnelMetrics.key_metrics.conversion_rate * 100,
  purchase_to_repeat: 25, // Estimated repeat purchase rate
  overall_funnel_efficiency: funnelMetrics.key_metrics.conversion_rate * 100
};

funnelMetrics.funnel_efficiency = funnelEfficiency;

return [{json: funnelMetrics}];

