parameters.jsCode:
// Process AI curation results and organize content
const aiResponse = JSON.parse($input.first().json.choices[0].message.content);

const organizedContent = {
  collection_timestamp: new Date().toISOString(),
  total_content_collected: aiResponse.total_items_analyzed || 0,
  high_quality_content: aiResponse.content_items.filter(item => 
    item.quality_assessment.visual_quality === 'HIGH' && 
    item.quality_assessment.relevance_to_brand === 'HIGH' &&
    item.sentiment === 'positive'
  ),
  categorized_content: {
    product_usage: aiResponse.content_items.filter(item => item.themes?.includes('product_usage')),
    customer_experience: aiResponse.content_items.filter(item => item.themes?.includes('customer_experience')),
    unboxing_reviews: aiResponse.content_items.filter(item => item.themes?.includes('unboxing')),
    testimonials: aiResponse.content_items.filter(item => item.themes?.includes('testimonial')),
    creative_content: aiResponse.content_items.filter(item => item.themes?.includes('creative'))
  },
  strategy_insights: aiResponse.strategy_insights,
  content_gaps: aiResponse.content_gaps || [],
  influencer_candidates: aiResponse.potential_influencers || []
};

// Add metadata to each content item
organizedContent.high_quality_content.forEach(item => {
  item.processed_at = new Date().toISOString();
  item.curation_status = 'APPROVED';
  item.rights_status = item.rights_clearance_needed ? 'PENDING' : 'CLEARED';
  item.usage_score = 
    (item.quality_assessment.authenticity_score + item.brand_alignment_score) / 2;
});

// Generate curation summary
const curationSummary = {
  total_collected: organizedContent.total_content_collected,
  approved_for_use: organizedContent.high_quality_content.length,
  approval_rate: ((organizedContent.high_quality_content.length / organizedContent.total_content_collected) * 100).toFixed(1) + '%',
  top_themes: Object.keys(organizedContent.categorized_content).sort((a,b) => 
    organizedContent.categorized_content[b].length - organizedContent.categorized_content[a].length
  ),
  trending_topics: organizedContent.strategy_insights?.trending_themes || []
};

organizedContent.summary = curationSummary;

return [{json: organizedContent}];

