// Process AI partnership analysis and categorize by priority
const aiResponse = JSON.parse($input.first().json.choices[0].message.content);

const relationshipInsights = {
  analysis_timestamp: new Date().toISOString(),
  total_partners_analyzed: aiResponse.partners_analyzed || 0,
  portfolio_health_score: aiResponse.portfolio_overall_health || 0,
  partner_segmentation: {
    champions: aiResponse.partner_analysis.filter(p => p.relationship_health_score >= 90),
    high_performers: aiResponse.partner_analysis.filter(p => p.relationship_health_score >= 75 && p.relationship_health_score < 90),
    steady_partners: aiResponse.partner_analysis.filter(p => p.relationship_health_score >= 60 && p.relationship_health_score < 75),
    at_risk: aiResponse.partner_analysis.filter(p => p.relationship_health_score >= 40 && p.relationship_health_score < 60),
    critical_attention: aiResponse.partner_analysis.filter(p => p.relationship_health_score < 40)
  },
  risk_assessment: {
    critical_risks: aiResponse.partner_analysis.filter(p => p.risk_level === 'CRITICAL'),
    high_risks: aiResponse.partner_analysis.filter(p => p.risk_level === 'HIGH'),
    medium_risks: aiResponse.partner_analysis.filter(p => p.risk_level === 'MEDIUM'),
    low_risks: aiResponse.partner_analysis.filter(p => p.risk_level === 'LOW')
  },
  opportunities: aiResponse.growth_opportunities || [],
  immediate_actions: aiResponse.portfolio_recommendations?.immediate_actions || [],
  strategic_initiatives: aiResponse.portfolio_recommendations?.strategic_initiatives || []
};

// Calculate portfolio metrics
const portfolioMetrics = {
  total_partners: relationshipInsights.total_partners_analyzed,
  healthy_partnerships: relationshipInsights.partner_segmentation.champions.length + relationshipInsights.partner_segmentation.high_performers.length,
  at_risk_partnerships: relationshipInsights.partner_segmentation.at_risk.length + relationshipInsights.partner_segmentation.critical_attention.length,
  health_distribution: {
    excellent: ((relationshipInsights.partner_segmentation.champions.length / relationshipInsights.total_partners_analyzed) * 100).toFixed(1) + '%',
    good: ((relationshipInsights.partner_segmentation.high_performers.length / relationshipInsights.total_partners_analyzed) * 100).toFixed(1) + '%',
    fair: ((relationshipInsights.partner_segmentation.steady_partners.length / relationshipInsights.total_partners_analyzed) * 100).toFixed(1) + '%',
    poor: ((relationshipInsights.partner_segmentation.at_risk.length / relationshipInsights.total_partners_analyzed) * 100).toFixed(1) + '%',
    critical: ((relationshipInsights.partner_segmentation.critical_attention.length / relationshipInsights.total_partners_analyzed) * 100).toFixed(1) + '%'
  }
};

relationshipInsights.portfolio_metrics = portfolioMetrics;

// Add priority levels to partners
Object.values(relationshipInsights.partner_segmentation).forEach(segment => {
  segment.forEach(partner => {
    partner.last_assessed = new Date().toISOString();
    partner.priority_level = 
      partner.relationship_health_score >= 90 ? 'MONITOR' :
      partner.relationship_health_score >= 75 ? 'ENGAGE' :
      partner.relationship_health_score >= 60 ? 'SUPPORT' :
      partner.relationship_health_score >= 40 ? 'INTERVENE' : 'CRITICAL';
  });
});

return [{json: relationshipInsights}];

