parameters.jsCode:
// Process AI pricing recommendations and categorize by impact
const aiResponse = JSON.parse($input.first().json.choices[0].message.content);

const pricingStrategy = {
  analysis_timestamp: new Date().toISOString(),
  total_products_analyzed: aiResponse.products_analyzed || 0,
  portfolio_revenue_impact: aiResponse.expected_portfolio_impact || 0,
  product_recommendations: {
    immediate_action: aiResponse.product_recommendations.filter(p => p.implementation_priority === 'IMMEDIATE' && p.revenue_impact > 0.05),
    high_impact: aiResponse.product_recommendations.filter(p => p.revenue_impact >= 0.10),
    medium_impact: aiResponse.product_recommendations.filter(p => p.revenue_impact >= 0.05 && p.revenue_impact < 0.10),
    low_impact: aiResponse.product_recommendations.filter(p => p.revenue_impact < 0.05),
    price_increases: aiResponse.product_recommendations.filter(p => p.price_change_percentage > 0),
    price_decreases: aiResponse.product_recommendations.filter(p => p.price_change_percentage < 0),
    discount_opportunities: aiResponse.product_recommendations.filter(p => p.discount_recommended === true)
  },
  discount_strategy: aiResponse.discount_strategy || {},
  risk_assessment: {
    low_risk: aiResponse.product_recommendations.filter(p => p.risk_level === 'LOW'),
    medium_risk: aiResponse.product_recommendations.filter(p => p.risk_level === 'MEDIUM'),
    high_risk: aiResponse.product_recommendations.filter(p => p.risk_level === 'HIGH')
  },
  implementation_plan: aiResponse.implementation_plan || {}
};

// Calculate portfolio metrics
const portfolioMetrics = {
  total_revenue_potential: pricingStrategy.product_recommendations.immediate_action.reduce((sum, p) => sum + p.expected_revenue_impact, 0),
  average_price_change: (pricingStrategy.product_recommendations.immediate_action.reduce((sum, p) => sum + p.price_change_percentage, 0) / pricingStrategy.product_recommendations.immediate_action.length).toFixed(2) + '%',
  high_impact_opportunities: pricingStrategy.product_recommendations.high_impact.length,
  discount_eligible_products: pricingStrategy.product_recommendations.discount_opportunities.length,
  risk_distribution: {
    low: ((pricingStrategy.risk_assessment.low_risk.length / pricingStrategy.total_products_analyzed) * 100).toFixed(1) + '%',
    medium: ((pricingStrategy.risk_assessment.medium_risk.length / pricingStrategy.total_products_analyzed) * 100).toFixed(1) + '%',
    high: ((pricingStrategy.risk_assessment.high_risk.length / pricingStrategy.total_products_analyzed) * 100).toFixed(1) + '%'
  }
};

pricingStrategy.portfolio_metrics = portfolioMetrics;

// Add implementation metadata
pricingStrategy.product_recommendations.immediate_action.forEach(product => {
  product.implementation_status = 'PENDING';
  product.scheduled_date = new Date(Date.now() + 24*60*60*1000).toISOString(); // Next day
  product.monitoring_period = '7 days';
  product.rollback_trigger = 'revenue_decline > 5%';
});

return [{json: pricingStrategy}];

