// Process AI PDP recommendations and categorize by impact
const aiResponse = JSON.parse($input.first().json.choices[0].message.content);

const pdpOptimization = {
  analysis_timestamp: new Date().toISOString(),
  total_listings_analyzed: aiResponse.listings_analyzed || 0,
  portfolio_conversion_potential: aiResponse.expected_portfolio_impact || 0,
  listing_optimizations: {
    critical_updates: aiResponse.product_optimizations.filter(p => p.priority_level === 'CRITICAL' && p.expected_conversion_impact > 0.15),
    high_impact: aiResponse.product_optimizations.filter(p => p.expected_conversion_impact >= 0.10),
    medium_impact: aiResponse.product_optimizations.filter(p => p.expected_conversion_impact >= 0.05 && p.expected_conversion_impact < 0.10),
    low_impact: aiResponse.product_optimizations.filter(p => p.expected_conversion_impact < 0.05),
    title_optimizations: aiResponse.product_optimizations.filter(p => p.title_optimization_needed === true),
    bullet_optimizations: aiResponse.product_optimizations.filter(p => p.bullet_optimization_needed === true),
    keyword_expansions: aiResponse.product_optimizations.filter(p => p.keyword_expansion_needed === true)
  },
  keyword_strategy: aiResponse.keyword_strategy || {},
  content_enhancement: aiResponse.content_enhancement_opportunities || [],
  competitive_insights: aiResponse.competitive_analysis || {}
};

// Calculate portfolio metrics
const portfolioMetrics = {
  total_conversion_potential: pdpOptimization.listing_optimizations.critical_updates.reduce((sum, p) => sum + p.expected_conversion_impact, 0),
  average_conversion_improvement: ((pdpOptimization.listing_optimizations.critical_updates.reduce((sum, p) => sum + p.expected_conversion_impact, 0) / pdpOptimization.listing_optimizations.critical_updates.length) * 100).toFixed(1) + '%',
  critical_updates_needed: pdpOptimization.listing_optimizations.critical_updates.length,
  high_impact_opportunities: pdpOptimization.listing_optimizations.high_impact.length,
  optimization_distribution: {
    titles: ((pdpOptimization.listing_optimizations.title_optimizations.length / pdpOptimization.total_listings_analyzed) * 100).toFixed(1) + '%',
    bullets: ((pdpOptimization.listing_optimizations.bullet_optimizations.length / pdpOptimization.total_listings_analyzed) * 100).toFixed(1) + '%',
    keywords: ((pdpOptimization.listing_optimizations.keyword_expansions.length / pdpOptimization.total_listings_analyzed) * 100).toFixed(1) + '%'
  }
};

pdpOptimization.portfolio_metrics = portfolioMetrics;

// Add implementation metadata
pdpOptimization.listing_optimizations.critical_updates.forEach(listing => {
  listing.implementation_status = 'PENDING';
  listing.scheduled_date = new Date(Date.now() + 24*60*60*1000).toISOString(); // Next day
  listing.monitoring_period = '14 days';
  listing.success_metrics = ['conversion_rate', 'sales_rank', 'search_impressions'];
  listing.rollback_trigger = 'conversion_decline > 10%';
});

return [{json: pdpOptimization}];

