// Process AI competitor analysis and categorize by priority
const aiResponse = JSON.parse($input.first().json.choices[0].message.content);

const competitiveIntelligence = {
  analysis_timestamp: new Date().toISOString(),
  total_competitors_analyzed: aiResponse.competitors_analyzed || 0,
  market_threat_level: aiResponse.overall_market_threat || 'MEDIUM',
  competitor_analysis: {
    critical_threats: aiResponse.competitor_insights.filter(c => c.threat_assessment.current_level === 'CRITICAL'),
    high_threats: aiResponse.competitor_insights.filter(c => c.threat_assessment.current_level === 'HIGH'),
    emerging_threats: aiResponse.competitor_insights.filter(c => c.threat_assessment.current_level === 'MEDIUM' && c.threat_assessment.trend === 'INCREASING'),
    opportunities: aiResponse.competitor_insights.filter(c => c.opportunity_potential >= 0.7),
    strategic_moves: aiResponse.competitor_insights.filter(c => c.recent_strategic_moves?.length > 0)
  },
  market_insights: aiResponse.market_analysis || {},
  strategic_recommendations: aiResponse.strategic_recommendations || [],
  predictive_intelligence: aiResponse.predictive_insights || {}
};

// Calculate market intelligence metrics
const marketMetrics = {
  total_threats: competitiveIntelligence.competitor_analysis.critical_threats.length + competitiveIntelligence.competitor_analysis.high_threats.length,
  opportunity_count: competitiveIntelligence.competitor_analysis.opportunities.length,
  strategic_moves_detected: competitiveIntelligence.competitor_analysis.strategic_moves.length,
  threat_distribution: {
    critical: ((competitiveIntelligence.competitor_analysis.critical_threats.length / competitiveIntelligence.total_competitors_analyzed) * 100).toFixed(1) + '%',
    high: ((competitiveIntelligence.competitor_analysis.high_threats.length / competitiveIntelligence.total_competitors_analyzed) * 100).toFixed(1) + '%',
    medium: ((competitiveIntelligence.competitor_analysis.emerging_threats.length / competitiveIntelligence.total_competitors_analyzed) * 100).toFixed(1) + '%'
  },
  market_dominance_shift: aiResponse.market_dominance_changes || 'STABLE'
};

competitiveIntelligence.market_metrics = marketMetrics;

// Add intelligence metadata
competitiveIntelligence.competitor_analysis.critical_threats.forEach(competitor => {
  competitor.intelligence_priority = 'IMMEDIATE';
  competitor.monitoring_frequency = 'DAILY';
  competitor.action_required = 'EXECUTIVE_REVIEW';
  competitor.last_updated = new Date().toISOString();
});

competitiveIntelligence.competitor_analysis.high_threats.forEach(competitor => {
  competitor.intelligence_priority = 'HIGH';
  competitor.monitoring_frequency = 'WEEKLY';
  competitor.action_required = 'TEAM_REVIEW';
  competitor.last_updated = new Date().toISOString();
});

return [{json: competitiveIntelligence}];

